mapper.insert:
insert into user (uid, password, name, 
  email, role, headImage
  )
values (#{uid,jdbcType=INTEGER}, #{password,jdbcType=VARCHAR}, #{name,jdbcType=VARCHAR}, 
  #{email,jdbcType=VARCHAR}, #{role,jdbcType=VARCHAR}, #{headimage,jdbcType=VARCHAR}
  )

mapper.select:
select uid, password, name, email, role, headImage
from user
where uid = #{uid,jdbcType=INTEGER}

select uid, password, name, email, role, headImage
from user
where name = #{name,jdbcType=VARCHAR}

select uid, password, name, email, role, headImage
from user
where uid = #{uid} and password=#{password}

select name
from user
where uid = #{uid,jdbcType=INTEGER}
